import { useState } from "react";
import { Offcanvas } from "react-bootstrap";

const SidebarButton = () => {

    const [isshow, setIsShow] = useState<any>(false);
    const [themeGradient, setThemeGradient] = useState('linear-gradient(310deg, #344767 0%, #344767 100%)');

    const switchGradient = (theme: string) => {
        let newGradient = theme;
        setThemeGradient(newGradient);
    };

    return (
        <div className="sidebar-bottom-btn-wrapper">
            <button className="sidebar-bottom-btn" type="button" onClick={() => setIsShow(true)}>
                <i className="fa-solid fa-gear"></i>
            </button>
            <Offcanvas show={isshow} placement={"end"} onHide={() => setIsShow(false)} className="size-md">
                <Offcanvas.Header closeButton className="border">
                    <Offcanvas.Title className="text-dark">Theme Setting</Offcanvas.Title>
                </Offcanvas.Header>
                <Offcanvas.Body>
                    <div className="col-lg-12 mb-lg-0 mb-4">
                        <div className="btn-toolbar justify-content-center" role="toolbar" aria-label="Toolbar with button groups">
                            {/* secondary */}
                            <button type="button" className="btn btn-secondary me-1" onClick={() => switchGradient('linear-gradient(310deg, #8392AB 0%, #8392AB 100%)')}></button>
                            {/* Black */}
                            <button type="button" className="btn btn-dark me-1" onClick={() => switchGradient('linear-gradient(310deg, #344767 0%, #344767 100%)')}></button>
                            {/* Red */}
                            <button type="button" className="btn btn-danger me-1" onClick={() => switchGradient('linear-gradient(310deg, #dd4e41 0%, #f12020)')}></button>
                            {/* Pink */}
                            <button type="button" className="btn btn-primary me-1" onClick={() => switchGradient('linear-gradient(310deg, #cb0c9f 0%, #cb0c9f 100%)')}></button>
                            {/* Blue */}
                            <button type="button" className="btn btn-info me-1" onClick={() => switchGradient('linear-gradient(310deg, #17c1e8 0%, #17c1e8')}></button>
                        </div>
                    </div>
                </Offcanvas.Body>
                <div className="offcanvas-footer border-top p-3 text-center hstack gap-2 justify-content-end">
                    <button type="submit" className="btn btn-primary" id="add-user-submit-btn">
                        Submit
                    </button>
                    <button className="btn btn-secondary" onClick={() => setIsShow(false)}>
                        Cancel
                    </button>
                </div>
            </Offcanvas>
        </div>
    );
};

export default SidebarButton;
