import React, { useEffect, useState } from "react";
import AddEditUser from "./AddEditUser";
import Pagination from "../../components/Paginaion/Pagination";
import { Modal, ModalFooter, ModalHeader } from "reactstrap";
import WebService from "../../utility/WebService";
import tim from "../../assets/img/tim.png";
import Search from "../../components/Search/Search";
import toast from "react-hot-toast";
import { limit } from "../../components/common/list";

const Userlist = () => {

    const [modelName, setModelName] = useState<any>({ name: "", id: 0 });
    const [list, setlist] = useState<any>([]);
    const [count, setCount] = useState(0);
    const [currentPage, setCurrentPage] = useState(1);
    const [filter, setFilter] = useState({ sortBy: "first_name", orderBy: "asc" });

    const handlePageChange = (page: number) => {
        setCurrentPage(page);
        getList(page, keyword);
        // Load data for the selected page
    };
    const [keyword, setKeyword] = useState("");

    useEffect(() => {
        getList(1, keyword);
    }, [keyword])

    const onCloseModal = () => {
        setModelName("");
    }

    const changeStatus = (id: any) => {
        let obj: any = {};
        obj.id = id;
        WebService.putAPI({
            action: "/update-user-status",
            body: obj
        })
            .then((res: any) => {
                if (res && res.message) {
                    toast.success(res.message);
                    getList(1, keyword);
                    setModelName("");
                }
            })
            .catch((error: any) => {
                // toast.error(error);
                //   setLoading(false);
            });
    }

    const handleSort = (field: any) => {
        if (filter.sortBy === field) {
            setFilter({ ...filter, orderBy: filter.orderBy === "asc" ? "desc" : "asc" });
        } else {
            setFilter({ ...filter, sortBy: field });
            setFilter({ ...filter, orderBy: "asc" });
        }
        getList(1, keyword);
    }

    const getList = (page?: any, keyword?: any) => {
        let obj: any = {};
        obj.offset = page - 1;
        obj.keyword = keyword;
        obj.limit = limit;
        obj.order_by = filter.orderBy;
        obj.sort_by = filter.sortBy;
        WebService.getAPI({
            action: "/user-list",
            body: obj
        })
            .then((res: any) => {
                if (res && res.list) {
                    setlist(res.list);
                    // setLoader(true);
                    setCount(res.count);
                }
            })
            .catch((error: any) => {
                // toast.error(error);
                //   setLoading(false);
            });
    }

    return (
        <>
                <div className="row">
                                        <div className="col-12">
                                            <div className="page-title-box d-sm-flex align-items-center justify-content-between">
                                                <h4 className="mb-sm-0">Basic Tables</h4>

                                                <div className="page-title-right">
                                                    <ol className="breadcrumb m-0">
                                                        <li className="breadcrumb-item"><a href="javascript: void(0);">Tables</a></li>
                                                        <li className="breadcrumb-item active">Basic Tables</li>
                                                    </ol>
                                                </div>

                                            </div>
                                        </div>
                                    </div>
        </>
    );
}

export default Userlist;
