import { useEffect, useState } from "react";
import Search from "../../components/Search/Search";
import Breadcrumb from "../../components/Breadcrumb/Breadcrumb";
import Table, { Cell, Row } from '../../components/Table/Table';
import { UncontrolledDropdown } from "reactstrap";
import { DropdownItem, DropdownMenu, DropdownToggle } from "react-bootstrap";

const Userlist = () => {

    const [modelName, setModelName] = useState<any>({ name: "", id: 0 });
    const [keyword, setKeyword] = useState("");
    const [rows, setRows] = useState<Row[]>([]);

    const onCloseModal = () => {
        setModelName("");
    }

    const localheaders = [
        {
            title: "Id",
            id: "id",
        },
        {
            title: "User Name",
            id: "userName",
        },
        {
            title: "Action",
        },
    ];

    const localdata = [
        {
            title: "User 1",
        },
        {
            title: "User 2",
        }
    ];

    const actionList = (row: any) => {
        return (
            // <div>
            //     <button className="btn btn-sm btn-primary me-1">Edit</button>
            //     <button className="btn btn-sm btn-danger">Delete</button>
            // </div>
            <ul className="list-inline  gap-2 mb-0">
                <li className="list-inline-item">
                    <UncontrolledDropdown>
                        <DropdownToggle className="btn btn-soft-secondary btn-sm dropdown" tag="button">
                            <i className="ri-more-fill align-middle"></i>
                        </DropdownToggle>
                        <DropdownMenu className="dropdown-menu-end">
                            <DropdownItem>
                                <i className="ri-delete-bin-line align-middle me-1 text-danger"></i>Delete
                            </DropdownItem>
                        </DropdownMenu>
                    </UncontrolledDropdown>
                </li>
            </ul>
        );
    };

    const getList = () => {
        const result: Row[] = [];

        for (let i = 0; i < localdata.length; i++) {
            const columns: Cell[] = [];
            columns.push({ value: i + 1 }); // ID
            columns.push({ value: localdata[i].title }); // Name
            columns.push({ value: actionList(localdata[i]) }); // Actions
            result.push({ data: columns });
        }

        setRows(result);
    };

    useEffect(() => {
        getList();
    }, []);

    return (
        <>
            <div className="row">
                <div className="col-12">
                    <Breadcrumb outerPage="Dashboard" mainPage="User Table" />
                </div>
                <div className="col-12">
                    <div className="card">
                        <div className="card-header align-items-center d-flex">
                            <div className="col-12 col-md-4 mb-2 mb-md-0">
                                <Search keyword={keyword} setKeyword={setKeyword} />
                            </div>
                            <div className="col-12 col-md-8 d-grid d-md-flex justify-content-md-end">
                                <button className="btn btn-light" onClick={() => { setModelName({ name: "AddEditUser", id: 0 }) }}>Add</button>
                            </div>
                        </div>
                        <div className="card-body">
                            <Table
                                Header={true}
                                HeaderList={localheaders}
                                rows={rows}
                                Checkbox={true}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Userlist;
