import { useNavigate } from 'react-router-dom';
import avatar1 from "../../assets/images/users/avatar-1.jpg";
import { Container, Dropdown, Nav, Navbar } from 'react-bootstrap';

interface HeaderProps {
    toggleSidebar: () => void;
}

const Header = (props: HeaderProps) => {

    const navigate = useNavigate();

    const userData = JSON.parse(localStorage.getItem("UserData") || "{}");
    // alert("userData", userData);

    const logout = () => {
        localStorage.removeItem('UserData');
        localStorage.removeItem('token');
        localStorage.clear();
        navigate("/login");
    }

    return (
        <Navbar bg="light" variant="light" expand="lg" className="px-3" style={{
            height: "72px", // same height as header
            backgroundColor: "#212529", // matches Bootstrap dark
        }}>
            <Container fluid>
                <button
                    className="btn btn-outline-dark d-xl-none"
                    onClick={props.toggleSidebar}
                >
                    â˜°
                </button>
                <Nav className="ms-auto">
                    <Dropdown align="end">
                        <Dropdown.Toggle
                            as="div" // <-- This lets us replace the button with our own HTML
                            className="d-flex align-items-center px-2 py-1 rounded cursor-pointer"
                            style={{
                                backgroundColor: "#f8f9fa",
                       border: "none", // No border
          minWidth: "150px"
                            }}
                        >
                            <img
                                src={avatar1}
                                alt="profile"
                                className="rounded-circle me-2"
                                style={{ width: "35px", height: "35px", objectFit: "cover" }}
                            />
                            <span style={{ fontWeight: "500" }}>John Doe</span>
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item href="#profile">Profile</Dropdown.Item>
                            <Dropdown.Item href="#settings">Settings</Dropdown.Item>
                            <Dropdown.Divider />
                            <Dropdown.Item href="#logout">Logout</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </Nav>
            </Container>
        </Navbar>
    );
}

export default Header;
