import React, { useEffect, useState } from "react";
import logosm from "../../assets/images/logo-sm.png";
import logodark from "../../assets/images/logo-dark.png";
import logolight from "../../assets/images/logo-light.png";
import { Link } from "react-router-dom";

interface SidebarProps {
    isOpen: boolean,
    toggleSidebar: any
}

const Sidebar = (props: SidebarProps) => {

    const [currentRoute, setCurrentRoute] = useState('Dashboard');

    useEffect(() => {
        const currentPath = window.location.pathname;
        const lastSegment = currentPath.split('/').filter(Boolean).pop();
        switch (lastSegment) {
            case "user":
                setCurrentRoute('User');
                break;
            case "product":
                setCurrentRoute('Product');
                break;
            case "setting":
                setCurrentRoute('Setting');
                break;
            case "dashboard":
                setCurrentRoute('Dashboard');
                break;
            default:
                setCurrentRoute('Dashboard');
        }
    }, [currentRoute]);

    return (
        <div className="app-menu navbar-menu">
            <div className="navbar-brand-box">
                <a href="index.html" className="logo logo-dark">
                    <span className="logo-sm">
                        <img src={logosm} alt="" height="22" />
                    </span>
                    <span className="logo-lg">
                        <img src={logodark} alt="" height="17" />
                    </span>
                </a>
                <a href="index.html" className="logo logo-light">
                    <span className="logo-sm">
                        <img src={logosm} alt="" height="22" />
                    </span>
                    <span className="logo-lg">
                        <img src={logolight} alt="" height="17" />
                    </span>
                </a>
                <button type="button" className="btn btn-sm p-0 fs-20 header-item float-end btn-vertical-sm-hover" id="vertical-hover">
                    <i className="ri-record-circle-line"></i>
                </button>
            </div>
            <i className="bi bi-x p-3 position-absolute end-0 top-1 d-xl-none" onClick={props.toggleSidebar}></i>
            <div id="scrollbar" className="h-100">
                <div className="container-fluid">
                    <div id="two-column-menu">
                    </div>
                    <ul className="navbar-nav" id="navbar-nav">
                        <li className="menu-title"><span data-key="t-menu">Menu</span></li>
                        <li className="nav-item">
                            <a className={`nav-link ${currentRoute === "Dashboard" ? 'nav-link menu-link active' : 'nav-link menu-link'}`} href={"/dashboard"} onClick={() => setCurrentRoute('Dashboard')}>
                                <i className="ri-dashboard-2-line"></i> <span data-key="t-widgets">Dashboards</span>
                            </a>
                        </li>
                        <li className="nav-item">
                            <a className={`nav-link ${currentRoute === "User" ? 'nav-link menu-link active' : 'nav-link menu-link'}`} href={"/user"} onClick={() => setCurrentRoute('User')}>
                                <i className="ri-account-circle-line"></i> <span data-key="t-widgets">User</span>
                            </a>
                        </li>
                    </ul>
                </div>
            </div>
        </div>
    );
}

export default Sidebar;
