import React, { useEffect, useState } from "react";
import AddEditUser from "./AddEditUser";
import Pagination from "../../components/Paginaion/Pagination";
import { Modal, ModalFooter, ModalHeader } from "reactstrap";
import WebService from "../../utility/WebService";
import tim from "../../assets/img/tim.png";
import Search from "../../components/Search/Search";
import toast from "react-hot-toast";
import { limit } from "../../components/common/list";
import Breadcrumb from "../../components/Breadcrumb/Breadcrumb";

const Userlist = () => {

    const [modelName, setModelName] = useState<any>({ name: "", id: 0 });
    const [list, setlist] = useState<any>([]);
    const [count, setCount] = useState(0);
    const [currentPage, setCurrentPage] = useState(1);
    const [filter, setFilter] = useState({ sortBy: "first_name", orderBy: "asc" });

    const handlePageChange = (page: number) => {
        setCurrentPage(page);
        getList(page, keyword);
        // Load data for the selected page
    };
    const [keyword, setKeyword] = useState("");

    useEffect(() => {
        getList(1, keyword);
    }, [keyword])

    const onCloseModal = () => {
        setModelName("");
    }

    const changeStatus = (id: any) => {
        let obj: any = {};
        obj.id = id;
        WebService.putAPI({
            action: "/update-user-status",
            body: obj
        })
            .then((res: any) => {
                if (res && res.message) {
                    toast.success(res.message);
                    getList(1, keyword);
                    setModelName("");
                }
            })
            .catch((error: any) => {
                // toast.error(error);
                //   setLoading(false);
            });
    }

    const handleSort = (field: any) => {
        if (filter.sortBy === field) {
            setFilter({ ...filter, orderBy: filter.orderBy === "asc" ? "desc" : "asc" });
        } else {
            setFilter({ ...filter, sortBy: field });
            setFilter({ ...filter, orderBy: "asc" });
        }
        getList(1, keyword);
    }

    const getList = (page?: any, keyword?: any) => {
        let obj: any = {};
        obj.offset = page - 1;
        obj.keyword = keyword;
        obj.limit = limit;
        obj.order_by = filter.orderBy;
        obj.sort_by = filter.sortBy;
        WebService.getAPI({
            action: "/user-list",
            body: obj
        })
            .then((res: any) => {
                if (res && res.list) {
                    setlist(res.list);
                    // setLoader(true);
                    setCount(res.count);
                }
            })
            .catch((error: any) => {
                // toast.error(error);
                //   setLoading(false);
            });
    }

    return (
        <>
            <div className="row">
            <div className="col-12">
                <Breadcrumb outerPage="Dashboard" mainPage="User Table" />
            </div>
            <div className="col-12">
                <div className="card">
                    <div className="card-header align-items-center d-flex">
                        <div className="col-12 col-md-4 mb-2 mb-md-0">
                            <Search keyword={keyword} setKeyword={setKeyword} />
                        </div>
                        <div className="col-12 col-md-8 d-grid d-md-flex justify-content-md-end">
                            <button className="btn btn-light" onClick={() => { setModelName({ name: "AddEditUser", id: 0 }) }}>Add</button>
                        </div>
                    </div>
                    <div className="card-body">

                    </div>
                </div>
            </div>
        </div>
           {
        modelName.name === 'AddEditUser' &&
            <AddEditUser
                show={modelName.name === 'AddEditUser' ? true : false}
                modalData={modelName}
                handleClose={() => onCloseModal()}
                getlist={() => getList(1, keyword)}
                id={modelName.id}
            />
        }
        </>
    );
}

export default Userlist;
