import { useEffect, useState } from "react";
import logosm from "../../assets/images/Lock.png";
import logodark from "../../assets/images/logo-dark.png";
import logolight from "../../assets/images/Locker.png";
import { Link } from "react-router-dom";
import { Nav, Offcanvas } from "react-bootstrap";

interface SidebarProps {
    isOpen: boolean,
    toggleSidebar: any
}

const Sidebar = (props: SidebarProps) => {

    const [currentRoute, setCurrentRoute] = useState('Dashboard');

    useEffect(() => {
        const currentPath = window.location.pathname;
        const lastSegment = currentPath.split('/').filter(Boolean).pop();
        switch (lastSegment) {
            case "user":
                setCurrentRoute('User');
                break;
            case "product":
                setCurrentRoute('Product');
                break;
            case "setting":
                setCurrentRoute('Setting');
                break;
            case "dashboard":
                setCurrentRoute('Dashboard');
                break;
            default:
                setCurrentRoute('Dashboard');
        }
    }, [currentRoute]);

    return (
        <Offcanvas
            show={props.isOpen}
            onHide={props.toggleSidebar}
            responsive="xl"
            className="bg-dark text-white"
        >
            {/* Logo Section */}
            <div className="d-flex align-items-center justify-content-center py-3 border-bottom">
                <img
                    src="https://via.placeholder.com/40"
                    alt="Logo"
                    className="me-2"
                />
                <h5 className="m-0">My App</h5>
            </div>

            <Offcanvas.Body className="p-0">
                {/* Main Menu */}
                <Nav className="flex-column pt-2">
                    <Nav.Item>
                        <Link
                            to="/dashboard"
                            className="nav-link text-white d-flex align-items-center px-3 py-2"
                        >
                             Dashboard
                        </Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link
                            to="/user"
                            className="nav-link text-white d-flex align-items-center px-3 py-2"
                        >
                             Users
                        </Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link
                            to="/reports"
                            className="nav-link text-white d-flex align-items-center px-3 py-2"
                        >
                             Reports
                        </Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link
                            to="/settings"
                            className="nav-link text-white d-flex align-items-center px-3 py-2"
                        >
                             Settings
                        </Link>
                    </Nav.Item>
                </Nav>
            </Offcanvas.Body>
        </Offcanvas>
    );
}

export default Sidebar;
