// Table.tsx
import React from 'react';

export interface Column {
  title: string;
  id?: string;
}

export interface Cell {
  value: React.ReactNode; // allows JSX or string/number
}

export interface Row {
  data: Cell[];
}

interface propData {
  Header: boolean;
  HeaderList: Column[];
  Checkbox: boolean;
  rows: Row[];
}

const Table = (props: propData) => {
  return (
    <table className="table mb-0">
      {props.Header && (
        <thead>
          <tr>
            {props.Checkbox && (
              <th scope="col">
                <div className="form-check">
                  <input
                    className="form-check-input"
                    type="checkbox"
                    id="checkAll"
                  />
                </div>
              </th>
            )}
            {props.HeaderList.map((col, i) => (
              <th scope="col" key={i}>
                {col.title}
              </th>
            ))}
          </tr>
        </thead>
      )}
      <tbody>
        {props.rows.map((row, rowIndex) => (
          <tr key={rowIndex}>
            {props.Checkbox && (
              <td>
                <div className="form-check">
                  <input
                    className="form-check-input"
                    type="checkbox"
                    id={`checkbox-${rowIndex}`}
                  />
                </div>
              </td>
            )}
            {row.data.map((cell, cellIndex) => (
              <td key={cellIndex}>{cell.value}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Table;
