import React, { useEffect, useState } from "react";
import Offcanvas from 'react-bootstrap/Offcanvas';
import { useForm } from "react-hook-form";
// Images
import tim from "../../assets/img/tim.png";
import WebService from "../../utility/WebService";
import toast from "react-hot-toast";
import Select from "../../components/Select/Select";
import { status } from "../../components/common/list";

interface propData {
    show: boolean,
    handleClose: any,
    modalData: any
    getlist: any
    id: any
}

const AddEditUser = (props: propData) => {

    const [image, setImage] = useState<any>();
    const [previewImage, setPreviewImage] = useState<any>();

    const [cities, setCities] = useState<any>([]);
    const [states, setStates] = useState<any>([]);
    const [countries, setCountries] = useState<any>([]);

    const {
        handleSubmit,
        register,
        reset,
        setValue,
        formState: { errors },
    } = useForm();

    useEffect(() => {
        getCitiesList();
        getStatesList();
        getCountriesList();
        if (props.id) {
            getUserDetails(props.id);
        }
    }, []);

    const oncloseBlade = () => {
        reset({});
        props.handleClose();
    };

    const handleChange = (name: string, object: any) => {
        return object;
    }

    const AddEditUser = (data: any) => {
        if (props.id > 0) {
            // Update existing user
            var obj: any = {};
            obj.id = props.id
            WebService.putAPI({
                action: `/update-user`,
                body: data,
                id: "add-user-submit-btn",
            })
                .then((res: any) => {
                    // console.log(res);
                    toast.success(res.message);
                    if (image && res.id) {
                        uploadlogo(res.id);
                    }
                    if (!image) {
                        props.handleClose(!props.show);
                    }
                    props.getlist();
                })
                .catch((e) => {
                    // console.log(e.message);
                    // setLoading(false);
                });
        } else {
            // Add new user
            WebService.postAPI({
                action: `/save-user`,
                body: data,
                id: "add-user-submit-btn",
            })
                .then((res: any) => {
                    toast.success(res.message);
                    if (image && res.id) {
                        uploadlogo(res.id);
                    }
                    if (!image) {
                        props.handleClose(!props.show);
                    }
                    props.getlist();
                })
                .catch((e) => {
                    // console.log(e.message);
                    // setLoading(false);
                });
        }
    };

    const onLoadImage = (e: any) => {
        const { files } = e.target;
        if (files && files[0]) {
            setImage(files[0]);
        }
        e.target.value = '';

        // to load image preview
        if (files && files[0]) {
            const file = files[0];
            const reader = new FileReader();
            reader.onload = function (e) {
                setPreviewImage(e.target?.result);
            };
            reader.readAsDataURL(file);
        } else {
            setPreviewImage(null);
        }
    }

    const uploadlogo = (id: any) => {

        WebService.fileUploadAPI({
            action: "/user-image-upload",
            key: "photo",
            file: image,
            body: {
                id: id,
            },
        })
            .then((res: any) => {
                if (res && res.status === "success") {
                    toast.success(res.message);
                    props.handleClose(!props.show);
                } else {
                    toast.error(res.message);
                }

            })
            .catch((e) => {
            });
    }

    const getUserDetails = (id: any) => {
        let obj = {
            id: id,
        };
        WebService.getAPI({
            action: `/user-details`,
            body: obj,
        })
            .then((res: any) => {
                if (res && res.result) {
                    reset(res.result);
                    // setImage(res.result.photo);
                    setPreviewImage(res.result.photourl);
                }
            })
            .catch((error: any) => {
                // toast.error(error);
                //   setLoading(false);
            });
    }

    const getCitiesList = () => {
        WebService.getAPI({
            action: "/cities-list",
        })
            .then((res: any) => {
                if (res && res.list) {
                    setCities(res.list);
                }
            })
            .catch((error: any) => {
                // toast.error(error);
                //   setLoading(false);
            });
    }

    const getStatesList = () => {
        WebService.getAPI({
            action: "/states-list",
        })
            .then((res: any) => {
                if (res && res.list) {
                    setStates(res.list);
                }
            })
            .catch((error: any) => {
                // toast.error(error);
                //   setLoading(false);
            });
    }

    const getCountriesList = () => {
        WebService.getAPI({
            action: "/countries-list",
        })
            .then((res: any) => {
                if (res && res.list) {
                    setCountries(res.list);
                }
            })
            .catch((error: any) => {
                // toast.error(error);
                //   setLoading(false);
            });
    }

    return (
        <>
            <Offcanvas show={props.show} placement={"end"} onHide={props.handleClose} className="size-md">
                <form onSubmit={handleSubmit(AddEditUser)} className="d-flex flex-column justify-content-end h-100">
                    <Offcanvas.Header closeButton className="bg-gradient-dark_theme">
                        <Offcanvas.Title className="text-white">{props.id > 0 ? "Edit User" : "Add User"}</Offcanvas.Title>
                    </Offcanvas.Header>
                    <Offcanvas.Body>
                   
                    </Offcanvas.Body>
                </form>
            </Offcanvas>
        </>
    );
}

export default AddEditUser;
