import { useEffect, useState } from "react";
import logosm from "../../assets/images/Lock.png";
import logodark from "../../assets/images/logo-dark.png";
import logolight from "../../assets/images/Locker.png";
import { Link } from "react-router-dom";
import { Nav, Offcanvas } from "react-bootstrap";
import { NavLink } from "react-router-dom";

interface SidebarProps {
    isOpen: boolean,
    toggleSidebar: any
}

const Sidebar = (props: SidebarProps) => {

    const [currentRoute, setCurrentRoute] = useState('Dashboard');

    useEffect(() => {
        const currentPath = window.location.pathname;
        const lastSegment = currentPath.split('/').filter(Boolean).pop();
        switch (lastSegment) {
            case "user":
                setCurrentRoute('User');
                break;
            case "product":
                setCurrentRoute('Product');
                break;
            case "setting":
                setCurrentRoute('Setting');
                break;
            case "dashboard":
                setCurrentRoute('Dashboard');
                break;
            default:
                setCurrentRoute('Dashboard');
        }
    }, [currentRoute]);

    return (
        <Offcanvas
            show={props.isOpen}
            onHide={props.toggleSidebar}
            responsive="xl"
            className="bg-white text-black custom-sidebar"
        >
            {/* Logo Section */}
            <div className="d-flex align-items-center justify-content-center py-3 border-bottom border-end bg-white">
                <img
                    src={logosm}
                    style={{
                        width: "40px",
                        height: "40px",
                        objectFit: "contain",
                    }}
                    alt="Logo"
                    className="me-2"
                />
            </div>

            <Offcanvas.Body className="p-0">
                {/* Main Menu */}
                <Nav className="flex-column pt-2">
                    <Nav.Item>
                        <NavLink
                            to="/dashboard"
                            className={({ isActive }) =>
                                `nav-link text-black d-flex align-items-center px-3 py-2 sidebar-link  ${isActive || currentRoute === "Dashboard" ? "active" : ""
                                }`
                            }
                            onClick={() => setCurrentRoute("Dashboard")}
                        >
                            <i className="fas fa-tachometer-alt me-2"></i>
                            <span>Dashboard</span>
                        </NavLink>
                    </Nav.Item>

                    <Nav.Item>
                        <NavLink
                            to="/user" // ✅ Corrected path for Users
                            className={({ isActive }) =>
                                `nav-link text-black d-flex align-items-center px-3 py-2 sidebar-link ${isActive || currentRoute === "Users" ? "active" : ""
                                }`
                            }
                            onClick={() => setCurrentRoute("Users")}
                        >
                            <i className="fas fa-users me-2"></i>
                            <span>Users</span>
                        </NavLink>
                    </Nav.Item>
                </Nav>
            </Offcanvas.Body>
        </Offcanvas>
    );
}

export default Sidebar;
