import { useState } from "react";
import AddEditUser from "../../pages/User/AddEditUser";
import { Offcanvas } from "react-bootstrap";

const SidebarButton = () => {

    const [modelName, setModelName] = useState<any>(false);

    return (
        <div className="sidebar-bottom-btn-wrapper">
            <button
                className="sidebar-bottom-btn"
                type="button"
                onClick={() => setModelName({ name: "AddEditUser", id: 0 })}
            >
                âš™
            </button>

            {/* Offcanvas Page */}
            <Offcanvas show={props.show} placement={"end"} onHide={props.onCloseClick} className="size-md">
                <Offcanvas.Header closeButton className="border">
                    <Offcanvas.Title className="text-dark">{"Add User"}</Offcanvas.Title>
                </Offcanvas.Header>
                <Offcanvas.Body>

                </Offcanvas.Body>
                <div className="offcanvas-footer border-top p-3 text-center hstack gap-2 justify-content-end">
                    <button type="submit" className="btn btn-primary" id="add-user-submit-btn">
                        Submit
                    </button>
                    <button className="btn btn-secondary" >
                        Cancel
                    </button>
                </div>
            </Offcanvas>
        </div>
    );
};

export default SidebarButton;
