import { useEffect, useState } from "react";
import logosm from "../../assets/images/Lock.png";
import logodark from "../../assets/images/logo-dark.png";
import logolight from "../../assets/images/Locker.png";
import { Link } from "react-router-dom";
import { Nav, Offcanvas } from "react-bootstrap";

interface SidebarProps {
    isOpen: boolean,
    toggleSidebar: any
}

const Sidebar = (props: SidebarProps) => {

    const [currentRoute, setCurrentRoute] = useState('Dashboard');

    useEffect(() => {
        const currentPath = window.location.pathname;
        const lastSegment = currentPath.split('/').filter(Boolean).pop();
        switch (lastSegment) {
            case "user":
                setCurrentRoute('User');
                break;
            case "product":
                setCurrentRoute('Product');
                break;
            case "setting":
                setCurrentRoute('Setting');
                break;
            case "dashboard":
                setCurrentRoute('Dashboard');
                break;
            default:
                setCurrentRoute('Dashboard');
        }
    }, [currentRoute]);

    return (
        <Offcanvas
            show={props.isOpen}
            onHide={props.onClose}
            responsive="xl"
            className="bg-dark text-white"
        >
            <Offcanvas.Header closeButton>
                <Offcanvas.Title>Menu</Offcanvas.Title>
            </Offcanvas.Header>
            <Offcanvas.Body>
                <Nav className="flex-column">
                    <Nav.Item>
                        <Link to="/dashboard" className="nav-link text-white">
                            Dashboard
                        </Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Link to="/user" className="nav-link text-white">
                            User
                        </Link>
                    </Nav.Item>
                </Nav>
            </Offcanvas.Body>
        </Offcanvas>
    );
}

export default Sidebar;
