import { useEffect, useState } from "react";
import Search from "../../components/Search/Search";
import Breadcrumb from "../../components/Breadcrumb/Breadcrumb";
import Table, { Cell, Row } from '../../components/Table/Table';
import { UncontrolledDropdown } from "reactstrap";
import { DropdownItem, DropdownMenu, DropdownToggle } from "react-bootstrap";
import AddEditUser from "./AddEditUser";

const Userlist = () => {

    const [modelName, setModelName] = useState<any>({ name: "", id: 0 });
    const [keyword, setKeyword] = useState("");
    const [rows, setRows] = useState<Row[]>([]);

    const onCloseModal = () => {
        setModelName("");
    }

    const localheaders = [
        {
            title: "Id",
            id: "id",
        },
        {
            title: "User Name",
            id: "userName",
        },
        {
            title: "Action",
        },
    ];

    const localdata = [
        {
            title: "User 1",
        },
        {
            title: "User 2",
        }
    ];

    const actionList = (row: any) => {
        return (
            <div className="dropdown ms-sm-3">
                <button
                    type="button"
                    className="btn btn-link p-0"
                    id={`dropdownMenuButton-${row.id}`} // unique id for accessibility
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                >
                    <i className="bi bi-dice-6 text-dark fs-16 align-middle"></i>
                </button>
                <ul
                    className="dropdown-menu dropdown-menu-end"
                    aria-labelledby={`dropdownMenuButton-${row.id}`}
                >
                    <li>
                        <a className="dropdown-item" href="pages-profile.html">
                            <i className="mdi mdi-account-circle text-muted fs-16 align-middle me-1"></i>
                            <span className="align-middle">Profile</span>
                        </a>
                    </li>
                </ul>
            </div>
        );
    };

    const getList = () => {
        const result: Row[] = [];

        for (let i = 0; i < localdata.length; i++) {
            const columns: Cell[] = [];
            columns.push({ value: i + 1 }); // ID
            columns.push({ value: localdata[i].title }); // Name
            columns.push({ value: actionList(localdata[i]) }); // Actions
            result.push({ data: columns });
        }

        setRows(result);
    };

    useEffect(() => {
        getList();
    }, []);

    return (
        <>
            <AddEditUser show={modelName.name == "AddEditUser" ? true : false} onCloseClick={() => setModelName("")} />
            <div className="row">
                {/* <div className="col-12">
                    <Breadcrumb outerPage="Dashboard" mainPage="User Table" />
                </div> */}
                <div className="col-12">
                    <div className="card">
                        <div className="card-header">
                            <div className="row align-items-center">
                                <div className="col-12 col-md-4 mb-2 mb-md-0">
                                    <Search keyword={keyword} setKeyword={setKeyword} />
                                </div>
                                <div className="col-12 col-md-8 d-grid d-md-flex justify-content-md-end">
                                    <button
                                        className="btn btn-primary"
                                        onClick={() => setModelName({ name: "AddEditUser", id: 0 })}
                                    >
                                        Add
                                    </button>
                                </div>
                            </div>
                        </div>
                        <div className="card-body">
                            <Table
                                Header={true}
                                HeaderList={localheaders}
                                rows={rows}
                                Checkbox={true}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Userlist;
